#include "display.h"
void space_out(int x){ //position of x
	int i;
		for(i=1;i<x;i++){
		cout<<" ";
		}
}

void under_lined(int y){ //position of x
	int i;
	for(i=1;i<y;i++){
		cout<<endl;
		}
}


